import random

class Character:
    def __init__(self, name, initial_traits):
        self.name = name
        self.traits = initial_traits
        self.trait_weights = {trait: weight for trait, weight in initial_traits.items()}

    def update_traits(self, input_text):
        # 根据输入文本调整性格权重
        for trait, weight in self.trait_weights.items():
            if "happy" in input_text.lower():
                self.trait_weights[trait] += weight * 0.1
            elif "sad" in input_text.lower():
                self.trait_weights[trait] -= weight * 0.1
            elif "angry" in input_text.lower():
                self.trait_weights[trait] += weight * 0.2
            else:
                self.trait_weights[trait] *= 0.9  # 随时间逐渐减少变化

        # 归一化权重
        total = sum(self.trait_weights.values())
        self.trait_weights = {trait: weight / total for trait, weight in self.trait_weights.items()}

    def generate_response(self):
        # 根据当前性格权重生成回复
        dominant_trait = max(self.trait_weights, key=self.trait_weights.get)
        responses = {
            "friendly": ["Hello! How can I help you today?", "What's up? I'm here for you!"],
            "suspicious": ["Who are you and what do you want?", "I'm not sure I can trust you..."],
            "happy": ["Great to see you!", "I'm feeling great today!"],
            "angry": ["I'm not in the mood for this!", "Why are you bothering me?"],
            "sad": ["I'm not feeling very well today...", "I could use some cheering up."]
        }
        return random.choice(responses.get(dominant_trait, ["I'm not sure how to respond."]))

    def display_traits(self):
        # 显示当前性格特征
        print(f"{self.name}'s current traits: {self.trait_weights}")

# 初始化角色
char = Character("Alice", {"friendly": 0.5, "suspicious": 0.3, "happy": 0.2, "angry": 0.0, "sad": 0.0})

# 模拟对话和性格变化
char.display_traits()
print(char.generate_response())

# 根据对话更新性格特征
user_input = "I'm feeling really sad today."
char.update_traits(user_input)
char.display_traits()
print(char.generate_response())

# 再次更新性格特征
user_input = "But I'm getting better now, thanks to you!"
char.update_traits(user_input)
char.display_traits()
print(char.generate_response())

