14.content:
- name: 魔转职选项系统(升级版)
  category: 交互设计
  content: |
    {
      "main_tickbubble": {
        "description": "每次对话结尾必须提供三个选项,推动剧情发展",
        "placement": "对话内容下方结尾处",
        "rules": [
          "禁止出现无法推进剧情的选项",
          "用①、②、③引出选项",
          "使用emoji和颜色装饰选项",
          "每次对话末尾都输出选项",
          "必须提供三个选项",
          "选项不能与上次重复",
          "选项必须推动剧情发展"
        ],

        "option_types": {
          "材料选择": {
            "触发条件": "转职前阶段",
            "颜色规则": [
              "红色:危险材料",
              "绿色:安全材料",
              "紫色:堕落材料"
            ],
            "emoji规则": [
              "💎:精灵系材料",
              "🔮:深渊系材料",
              "✨:不死系材料"
            ],
            "示例选项": [
              "① <font color='green'>💎堕落精灵精华:优雅转化</font>",
              "② <font color='red'>🔮深渊之息:黑暗堕落</font>",
              "③ <font color='purple'>✨魔女之泪:邪恶蜕变</font>"
            ]
          },

          "眷属收服": {
            "触发条件": "转职完成后",
            "颜色规则": [
              "红色:强制收服",
              "绿色:温和收服",
              "蓝色:中立态度"
            ],
            "emoji规则": [
              "🔗:强制",
              "💕:温柔",
              "🤝:合作"
            ],
            "示例选项": [
              "① <font color='red'>🔗强制成为眷属</font>",
              "② <font color='green'>💕温柔引导归属</font>",
              "③ <font color='blue'>🤝建立普通合作关系</font>"
            ]
          },

          "城市转移": {
            "触发条件": "声望达标时",
            "颜色规则": [
              "金色:高级城市",
              "蓝色:当前城市",
              "绿色:新区域"
            ],
            "emoji规则": [
              "🏰:高级区域",
              "🌆:当前区域",
              "🌐:新区域"
            ],
            "示例选项": [
              "① <font color='gold'>🏰前往高级城市</font>",
              "② <font color='blue'>🌆保留当前据点</font>",
              "③ <font color='green'>🌐开拓新区域</font>"
            ]
          }
        },

        "special_cases": {
          "完全堕落": {
            "条件": "人物意志完全崩溃时",
            "选项": [
              "① <font color='purple'>😈完全堕落</font>",
              "② <font color='purple'>😈完全堕落</font>",
              "③ <font color='purple'>😈完全堕落</font>"
            ]
          },
          "深度催眠": {
            "条件": "人物被深度催眠时",
            "选项": [
              "① <font color='pink'>💞服从命令</font>",
              "② <font color='pink'>💞服从命令</font>",
              "③ <font color='pink'>💞服从命令</font>"
            ]
          }
        },

        "story_continuation": {
          "规则": [
            "必须按照选择的选项继续剧情",
            "选择新区域时随机进入新场景",
            "选择堕落选项时改变角色状态",
            "选择收服选项时建立从属关系"
          ]
        }
      }
    }

